INSERT INTO `email_templates_default_data` VALUES ('MANUAL_PAYMENT_NOTIFICATION','en_US','Manual Payment Notification','A manual payment needs to be processed for the journal {$journalName} and the user {$userFullName} (username \"{$userName}\").\n\nThe item being paid for is \"{$itemName}\".\nThe cost is {$itemCost} ({$itemCurrencyCode}).\n\nThis email was generated by Open Journal Systems\' Manual Payment plugin.','This email template is used to notify a journal manager contact that a manual payment was requested.');
INSERT INTO `email_templates_default_data` VALUES ('PAYPAL_INVESTIGATE_PAYMENT','en_US','Unusual PayPal Activity','Open Journal Systems has encountered unusual activity relating to PayPal payment support for the journal {$journalName}. This activity may need further investigation or manual intervention.\n                       \nThis email was generated by Open Journal Systems\' PayPal plugin.\n\nFull post information for the request:\n{$postInfo}\n\nAdditional information (if supplied):\n{$additionalInfo}\n\nServer vars:\n{$serverVars}\n','This email template is used to notify a journal\'s primary contact that suspicious activity or activity requiring manual intervention was encountered by the PayPal plugin.');
INSERT INTO `email_templates_default_data` VALUES ('DOAJ_EMAIL_REP','en_US','DOAJ Journal Information Submission','Dear Sonja, \n			\nThis message is to notify you of our interest to include {$journalName} in DOAJ.  To increase the speed of the reviewing process we would like to provide you with the following information about our journal.\n\nOpen access (does not charge readers or their institutions for access) (yes/no): {$isOpenAccess}\n\nJournal title: {$journalName}\n\nAlternative journal title (use this if the journal is having a well known 2nd title): {$altTitle}\n\nURL: {$journalURL}\n\nDo you charge the authors for publishing? (yes/no): {$hasAuthorFee}\n\nDo you have an URL (link) to an info page regarding information to authors? (URL): {$infoURL}\n \nPeer-review or editorial control (yes/no): {$isPeerReviewed}\n\nResearch journal that reports mainly original results of research or overviews of research (yes/no): {$isOriginalResearch}\n\nAcademic/scholarly community as the main target group (yes/no): {$isAcademic}\n\nActive (yes/no): {$isActive}\n\nExists also in printed version (yes/no): {$hasPrintedForm}\n\nEmbargo (if printed version is published before the online version) (yes/no): {$hasEmbargo}\n\nFull text access from year: {$accessFrom}\n\nVolume (first full text accessible volume): {$firstVolume}\n\nIssue (first full text accessible issue): {$firstIssue}\n\nISSN (ONLY fill this in if the journal has an ISSN): {$issn}\n\nEISSN (ONLY fill this in if the journal has an EISSN): {$eissn}\n\nPublisher: {$publisherName}\n\nCountry: {$country}\n\nLanguages: {$languages}\n\nKeywords (three-five words describing the main content of the journal): {$keywords}\n\nContact person (name of a contact person, could be the editor): {$contactName}\n\nContact e-mail (the e-mail address of the contact person): {$contactEmail}\n\nPublishing frequency / year: {$frequency}\n\nNumber of articles / issue: {$articlesPerIssue}\n\nPlease keep in mind that DOAJ only includes titles that live up to their definitions and selection criteria (http://www.doaj.org/articles/about#definitions).\n\nWhat DOAJ won\'t include: \n	- Newsletters that mainly inform about an institution. \n	- Journals that charge any access or subscription fees. \n	- Journals that have an embargo period. \n\nThank you very much! \nSincerely,\n{$editorialContactSignature}','This email template is used to send an email to the contact at the DOAJ, to include the journal in their archives.');
INSERT INTO `email_templates_default_data` VALUES ('THESIS_ABSTRACT_CONFIRM','en_US','Thesis Abstract Submission','This email was automatically generated by the {$journalName} thesis abstract submission form.\n\nPlease confirm that the submitted information is correct. Upon receiving your confirmation, the abstract will be published in the online edition of the journal.\n\nSimply reply to {$thesisName} ({$thesisEmail}) with the contents of this message and your confirmation, as well as any recommended clarifications or corrections.\n\nThank you.\n\n\nTitle : {$title} \nAuthor : {$studentName}\nDegree : {$degree}\nDegree Name: {$degreeName}\nDepartment : {$department}\nUniversity : {$university}\nAcceptance Date : {$dateApproved}\nSupervisor : {$supervisorName}\n\nAbstract\n--------\n{$abstract}\n\n{$thesisContactSignature}','This email template is used to confirm a thesis abstract submission by a student. It is sent to the student\'s supervisor, who is asked to confirm the details of the submitted thesis abstract.');
INSERT INTO `email_templates_default_data` VALUES ('SUBSCRIPTION_RENEW_INDL','en_US','Subscription Renewal: Individual','An individual subscription has been renewed online for {$journalName} with the following details.\n\nSubscription Type:\n{$subscriptionType}\n\nUser:\n{$userDetails}\n\nMembership Information (if provided):\n{$membership}\n\nTo view or edit this subscription, please use the following URL.\n\nSubscription URL: {$subscriptionUrl}\n','This email notifies the Subscription Manager that an individual subscription has been renewed online. It provides summary information about the subscription and a quick access link to the renewed subscription.');
INSERT INTO `email_templates_default_data` VALUES ('SUBSCRIPTION_RENEW_INSTL','en_US','Subscription Renewal: Institutional','An institutional subscription has been renewed online for {$journalName} with the following details.\n\nSubscription Type:\n{$subscriptionType}\n\nInstitution:\n{$institutionName}\n{$institutionMailingAddress}\n\nDomain (if provided):\n{$domain}\n\nIP Ranges (if provided):\n{$ipRanges}\n\nContact Person:\n{$userDetails}\n\nMembership Information (if provided):\n{$membership}\n\nTo view or edit this subscription, please use the following URL.\n\nSubscription URL: {$subscriptionUrl}\n','This email notifies the Subscription Manager that an institutional subscription has been renewed online. It provides summary information about the subscription and a quick access link to the renewed subscription.');
INSERT INTO `email_templates_default_data` VALUES ('SUBSCRIPTION_PURCHASE_INDL','en_US','Subscription Purchase: Individual','An individual subscription has been purchased online for {$journalName} with the following details.\n\nSubscription Type:\n{$subscriptionType}\n\nUser:\n{$userDetails}\n\nMembership Information (if provided):\n{$membership}\n\nTo view or edit this subscription, please use the following URL.\n\nSubscription URL: {$subscriptionUrl}\n','This email notifies the Subscription Manager that an individual subscription has been purchased online. It provides summary information about the subscription and a quick access link to the purchased subscription.');
INSERT INTO `email_templates_default_data` VALUES ('SUBSCRIPTION_PURCHASE_INSTL','en_US','Subscription Purchase: Institutional','An institutional subscription has been purchased online for {$journalName} with the following details. To activate this subscription, please use the provided Subscription URL and set the subscription status to \'Active\'.\n\nSubscription Type:\n{$subscriptionType}\n\nInstitution:\n{$institutionName}\n{$institutionMailingAddress}\n\nDomain (if provided):\n{$domain}\n\nIP Ranges (if provided):\n{$ipRanges}\n\nContact Person:\n{$userDetails}\n\nMembership Information (if provided):\n{$membership}\n\nTo view or edit this subscription, please use the following URL.\n\nSubscription URL: {$subscriptionUrl}\n','This email notifies the Subscription Manager that an institutional subscription has been purchased online. It provides summary information about the subscription and a quick access link to the purchased subscription.');
INSERT INTO `email_templates_default_data` VALUES ('SUBSCRIPTION_AFTER_EXPIRY','en_US','Subscription Expired','{$subscriberName}:\n\nYour {$journalName} subscription has expired.\n\n{$subscriptionType}\nExpiry date: {$expiryDate}\n\nTo renew your subscription, please go to the journal website. You are able to log in to the system with your username, \"{$username}\".\n\nIf you have any questions, please feel free to contact me.\n\n{$subscriptionContactSignature}','This email notifies a subscriber that their subscription has expired. It provides the journal\'s URL along with instructions for access.');
INSERT INTO `email_templates_default_data` VALUES ('SUBSCRIPTION_AFTER_EXPIRY_LAST','en_US','Subscription Expired - Final Reminder','{$subscriberName}:\n\nYour {$journalName} subscription has expired.\nPlease note that this is the final reminder that will be emailed to you.\n\n{$subscriptionType}\nExpiry date: {$expiryDate}\n\nTo renew your subscription, please go to the journal website. You are able to log in to the system with your username, \"{$username}\".\n\nIf you have any questions, please feel free to contact me.\n\n{$subscriptionContactSignature}','This email notifies a subscriber that their subscription has expired. It provides the journal\'s URL along with instructions for access.');
INSERT INTO `email_templates_default_data` VALUES ('SUBSCRIPTION_BEFORE_EXPIRY','en_US','Notice of Subscription Expiry','{$subscriberName}:\n\nYour {$journalName} subscription is about to expire.\n\n{$subscriptionType}\nExpiry date: {$expiryDate}\n\nTo ensure the continuity of your access to this journal, please go to the journal website and renew your subscription. You are able to log in to the system with your username, \"{$username}\".\n\nIf you have any questions, please feel free to contact me.\n\n{$subscriptionContactSignature}','This email notifies a subscriber that their subscription will soon expire. It provides the journal\'s URL along with instructions for access.');
INSERT INTO `email_templates_default_data` VALUES ('OPEN_ACCESS_NOTIFY','en_US','Issue Now Open Access','Readers:\n\n{$journalName} has just made available in an open access format the following issue. We invite you to review the Table of Contents here and then visit our web site ({$journalUrl}) to review articles and items of interest.\n\nThanks for the continuing interest in our work,\n{$editorialContactSignature}','This email is sent to registered readers who have requested to receive a notification email when an issue becomes open access.');
INSERT INTO `email_templates_default_data` VALUES ('EMAIL_LINK','en_US','Article of Possible Interest','Thought you might be interested in seeing \"{$articleTitle}\" by {$authorName} published in Vol {$volume}, No {$number} ({$year}) of {$journalName} at \"{$articleUrl}\".','This email template provides a registered reader with the opportunity to send information about an article to somebody who may be interested. It is available via the Reading Tools and must be enabled by the Journal Manager in the Reading Tools Administration page.');
INSERT INTO `email_templates_default_data` VALUES ('SUBSCRIPTION_NOTIFY','en_US','Subscription Notification','{$subscriberName}:\n\nYou have now been registered as a subscriber in our online journal management system for {$journalName}, with the following subscription:\n\n{$subscriptionType}\n\nTo access content that is available only to subscribers, simply log in to the system with your username, \"{$username}\".\n\nOnce you have logged in to the system you can change your profile details and password at any point.\n\nPlease note that if you have an institutional subscription, there is no need for users at your institution to log in, since requests for subscription content will be automatically authenticated by the system.\n\nIf you have any questions, please feel free to contact me.\n\n{$subscriptionContactSignature}','This email notifies a registered reader that the Manager has created a subscription for them. It provides the journal\'s URL along with instructions for access.');
INSERT INTO `email_templates_default_data` VALUES ('PROOFREAD_LAYOUT_COMPLETE','en_US','Proofreading Completed (Layout Editor)','{$editorialContactName}:\n\nThe galleys have now been corrected, following their proofreading, for the manuscript, \"{$articleTitle},\" for {$journalName}. This piece is now ready to publish.\n\n{$layoutEditorName}','This email from the Layout Editor to the Section Editor notifies them that the final stage of proofreading has been completed and the article is now ready to publish.');
INSERT INTO `email_templates_default_data` VALUES ('PROOFREAD_LAYOUT_ACK','en_US','Proofreading Acknowledgement (Layout Editor)','{$layoutEditorName}:\n\nThank you for completing the proofreading corrections for the galleys associated with the manuscript, \"{$articleTitle},\" for {$journalName}. This represents an important contribution to the work of this journal.\n\n{$editorialContactSignature}','This email from the Section Editor to the Layout Editor acknowledges completion of the final stage of proofreading and thanks them for their contribution.');
INSERT INTO `email_templates_default_data` VALUES ('PROOFREAD_ACK','en_US','Proofreading Acknowledgement','{$proofreaderName}:\n\nThank you for proofreading the galleys for the manuscript, \"{$articleTitle},\" for {$journalName}. This work makes an important contribution to the quality of this journal.\n\n{$editorialContactSignature}','This email from the Section Editor to the Proofreader confirms completion of the proofreader\'s proofreading process and thanks them for their contribution.');
INSERT INTO `email_templates_default_data` VALUES ('PROOFREAD_LAYOUT_REQUEST','en_US','Proofreading Request (Layout Editor)','{$layoutEditorName}:\n\nThe submission \"{$articleTitle}\" to {$journalName} has been proofread by the author and proofreader, and any corrections should now be made by following these steps.\n1. Click on the Submission URL below.\n2. Log into the journal consult Proofreading Corrections to create corrected galleys.\n3. Upload the revised galleys.\n4. Send the COMPLETE email in Proofreading Step 3 to the editor.\n\n{$journalName} URL: {$journalUrl}\nSubnmission URL: {$submissionUrl}\nUsername: {$layoutEditorUsername}\n\nIf you are unable to undertake this work at this time or have any questions, please contact me. Thank you for your contribution to this journal.\n\n{$editorialContactSignature}','This email from the Section Editor to the Layout Editor notifies them that an article\'s galleys are ready for final proofreading. It provides information on the article and how to access it.');
INSERT INTO `email_templates_default_data` VALUES ('PROOFREAD_COMPLETE','en_US','Proofreading Completed','{$editorialContactName}:\n\nI have completed proofreading the galleys for the manuscript, \"{$articleTitle},\" for {$journalName}. The galleys are now ready to have any final corrections made by the Layout Editor.\n\n{$proofreaderName}','This email from the Proofreader to the Section Editor notifies them that the Proofreader has completed the proofreading process.');
INSERT INTO `email_templates_default_data` VALUES ('PROOFREAD_REQUEST','en_US','Proofreading Request','{$proofreaderName}:\n\nThe submission \"{$articleTitle}\" to {$journalName} now needs to be proofread by following these steps.\n1. Click on the Submission URL below.\n2. Log into the journal and view PROOFING INSTRUCTIONS.\n3. Click on VIEW PROOF in Layout and proof the galley in the one or more formats used.\n4. Enter corrections (typographical and format) in Proofreading Corrections.\n5. Save and email corrections to Layout Editor.\n6. Send the COMPLETE email to the editor.\n\nManuscript URL: {$submissionUrl}\nUsername: {$proofreaderUsername}\n\nIf you are unable to undertake this work at this time or have any questions, please contact me. Thank you for your contribution to this journal.\n\n{$editorialContactSignature}','This email from the Section Editor to the Proofreader requests that they perform proofreading of an article\'s galleys. It provides information about the article and how to access it.');
INSERT INTO `email_templates_default_data` VALUES ('PROOFREAD_AUTHOR_ACK','en_US','Proofreading Acknowledgement (Author)','{$authorName}:\n\nThank you for proofreading the galleys for your manuscript, \"{$articleTitle},\" in {$journalName}. We are looking forward to publishing your work shortly.\n\nIf you subscribe to our notification service, you will receive an email of the Table of Contents as soon as it is published. If you have any questions, please contact me.\n\n{$editorialContactSignature}','This email from the Section Editor to the Author acknowledges completion of the initial proofreading process and thanks them for their contribution.');
INSERT INTO `email_templates_default_data` VALUES ('PROOFREAD_AUTHOR_COMPLETE','en_US','Proofreading Completed (Author)','{$editorialContactName}:\n\nI have completed proofreading the galleys for my manuscript, \"{$articleTitle},\" for {$journalName}. The galleys are now ready to have any final corrections made by the Proofreader and Layout Editor.\n\n{$authorName}','This email from the Author to the Proofreader and Editor notifies them that the Author\'s round of proofreading is complete and that details can be found in the article comments.');
INSERT INTO `email_templates_default_data` VALUES ('PROOFREAD_AUTHOR_REQUEST','en_US','Proofreading Request (Author)','{$authorName}:\n\nYour submission \"{$articleTitle}\" to {$journalName} now needs to be proofread by folllowing these steps.\n1. Click on the Submission URL below.\n2. Log into the journal and view PROOFING INSTRUCTIONS\n3. Click on VIEW PROOF in Layout and proof the galley in the one or more formats used.\n4. Enter corrections (typographical and format) in Proofreading Corrections.\n5. Save and email corrections to Layout Editor and Proofreader.\n6. Send the COMPLETE email to the editor.\n\nSubmission URL: {$submissionUrl}\nUsername: {$authorUsername}\n\n{$editorialContactSignature}','This email from the Section Editor to the Author notifies them that an article\'s galleys are ready for proofreading. It provides information about the article and how to access it.');
INSERT INTO `email_templates_default_data` VALUES ('COPYEDIT_FINAL_ACK','en_US','Copyediting Final Review Acknowledgement','{$copyeditorName}:\n\nThank you for completing the copyediting of the manuscript, \"{$articleTitle},\" for {$journalName}. This preparation of a \"clean copy\" for Layout is an important step in the editorial process.\n\n{$editorialContactSignature}','This email from the Section Editor to the Copyeditor acknowledges completion of the final round of copyediting and thanks them for their contribution.');
INSERT INTO `email_templates_default_data` VALUES ('LAYOUT_REQUEST','en_US','Request Galleys','{$layoutEditorName}:\n\nThe submission \"{$articleTitle}\" to {$journalName} now needs galleys laid out by following these steps.\n1. Click on the Submission URL below.\n2. Log into the journal and use the Layout Version file to create the galleys according to the journal\'s standards.\n3. Send the COMPLETE email to the editor.\n\n{$journalName} URL: {$journalUrl}\nSubmission URL: {$submissionLayoutUrl}\nUsername: {$layoutEditorUsername}\n\nIf you are unable to undertake this work at this time or have any questions, please contact me. Thank you for your contribution to this journal.\n\n{$editorialContactSignature}','This email from the Section Editor to the Layout Editor notifies them that they have been assigned the task of performing layout editing on a submission. It provides information about the submission and how to access it.');
INSERT INTO `email_templates_default_data` VALUES ('LAYOUT_COMPLETE','en_US','Galleys Complete','{$editorialContactName}:\n\nGalleys have now been prepared for the manuscript, \"{$articleTitle},\" for {$journalName} and are ready for proofreading. \n\nIf you have any questions, please contact me.\n\n{$layoutEditorName}','This email from the Layout Editor to the Section Editor notifies them that the layout process has been completed.');
INSERT INTO `email_templates_default_data` VALUES ('LAYOUT_ACK','en_US','Layout Acknowledgement','{$layoutEditorName}:\n\nThank you for preparing the galleys for the manuscript, \"{$articleTitle},\" for {$journalName}. This is an important contribution to the publishing process.\n\n{$editorialContactSignature}','This email from the Section Editor to the Layout Editor acknowledges completion of the layout editing process and thanks the layout editor for their contribution.');
INSERT INTO `email_templates_default_data` VALUES ('COPYEDIT_FINAL_COMPLETE','en_US','Copyediting Final Review Completed','{$editorialContactName}:\n\nI have now prepared a clean, copyedited version of the manuscript, \"{$articleTitle},\" for {$journalName}. It is ready for Layout and the preparation of the galleys.\n\n{$copyeditorName}','This email from the Copyeditor to the Section Editor notifies them that the final round of copyediting has been completed and that the layout process may now begin.');
INSERT INTO `email_templates_default_data` VALUES ('COPYEDIT_AUTHOR_ACK','en_US','Copyediting Review Acknowledgement','{$authorName}:\n\nThank you for reviewing the copyediting of your manuscript, \"{$articleTitle},\" for {$journalName}. We look forward to publishing this work.\n\n{$editorialContactSignature}','This email is sent by the Section Editor to a submission\'s Author to confirm completion of the Author\'s copyediting process and thank them for their contribution.');
INSERT INTO `email_templates_default_data` VALUES ('COPYEDIT_FINAL_REQUEST','en_US','Copyediting Final Review','{$copyeditorName}:\n\nThe author and editor have now completed their copyediting review of \"{$articleTitle}\" for {$journalName}. A \"clean copy\" now needs to be prepared for Layout by going through the following steps.\n1. Click on the Submission URL below.\n2. Log into the journal and click on the File that appears in Step 2.\n3. Open the downloaded file and incorporate all copyedits and query responses.\n4. Save clean file, and upload to Step 3 of Copyediting.\n5. Click on METADATA to check indexing information (saving any corrections).\n6. Send the COMPLETE email to the editor.\n\n{$journalName} URL: {$journalUrl}\nSubmission URL: {$submissionCopyeditingUrl}\nUsername: {$copyeditorUsername}\n\n{$editorialContactSignature}','This email from the Section Editor to the Copyeditor requests that they perform a final round of copyediting on a submission before it enters the layout process.');
INSERT INTO `email_templates_default_data` VALUES ('COPYEDIT_AUTHOR_COMPLETE','en_US','Copyediting Review Completed','{$editorialContactName}:\n\nI have now reviewed the copyediting of the manuscript, \"{$articleTitle},\" for {$journalName}, and it is ready for the final round of copyediting and preparation for Layout.\n\nThank you for this contribution to my work,\n{$authorName}','This email is sent by the Author to the Section Editor to notify them that the Author\'s copyediting process has been completed.');
INSERT INTO `email_templates_default_data` VALUES ('COPYEDIT_ACK','en_US','Copyediting Acknowledgement','{$copyeditorName}:\n\nThank you for copyediting the manuscript, \"{$articleTitle},\" for {$journalName}. It will make an important contribution to the quality of this journal.\n\n{$editorialContactSignature}','This email is sent by the Section Editor to a submission\'s Copyeditor to acknowledge that the Copyeditor has successfully completed the copyediting process and thank them for their contribution.');
INSERT INTO `email_templates_default_data` VALUES ('COPYEDIT_AUTHOR_REQUEST','en_US','Copyediting Review Request','{$authorName}:\n\nYour submission \"{$articleTitle}\" for {$journalName} has been through the first step of copyediting, and is available for you to review by following these steps.\n\n1. Click on the Submission URL below.\n2. Log into the journal and click on the File that appears in Step 1.\n3. Open the downloaded submission.\n4. Review the text, including copyediting proposals and Author Queries.\n5. Make any copyediting changes that would further improve the text.\n6. When completed, upload the file in Step 2.\n7. Click on METADATA to check indexing information for completeness and accuracy.\n8. Send the COMPLETE email to the editor and copyeditor.\n\nSubmission URL: {$submissionCopyeditingUrl}\nUsername: {$authorUsername}\n\nThis is the last opportunity to make substantial copyediting changes to the submission. The proofreading stage, that follows the preparation of the galleys, is restricted to correcting typographical and layout errors.\n\nIf you are unable to undertake this work at this time or have any questions, please contact me. Thank you for your contribution to this journal.\n\n{$editorialContactSignature}','This email is sent by the Section Editor to a submission\'s Author to request that they proofread the work of the copyeditor. It provides access information for the manuscript and warns that this is the last opportunity the author has to make substantial changes.');
INSERT INTO `email_templates_default_data` VALUES ('COPYEDIT_COMPLETE','en_US','Copyediting Completed','{$editorialContactName}:\n\nWe have now copyedited your submission \"{$articleTitle}\" for {$journalName}. To review the proposed changes and respond to Author Queries, please follow these steps:\n\n1. Log into the journal using URL below with your username and password (use Forgot link if needed).\n2. Click on the file at 1. Initial Copyedit File to download and open copyedited version. \n3. Review the copyediting, making changes using Track Changes in Word, and answer queries. \n4. Save file to desktop and upload it in 2. Author Copyedit.\n5. Click the email icon under COMPLETE and send email to the editor.\n\nThis is the last opportunity that you have to make substantial changes. You will be asked at a later stage to proofread the galleys, but at that point only minor typographical and layout errors can be corrected.\n\nManuscript URL: {$submissionEditingUrl}\nUsername: {$authorUsername}\n\nIf you are unable to undertake this work at this time or have any questions,\nplease contact me. Thank you for your contribution to this journal.\n\n{$copyeditorName}','');
INSERT INTO `email_templates_default_data` VALUES ('EDITOR_DECISION_REVISIONS','en_US','Editor Decision','{$authorName}:\n\nWe have reached a decision regarding your submission to {$journalTitle}, \"{$articleTitle}\".\n\nOur decision is to:\n\n{$editorialContactSignature}\n','This email from the Editor or Section Editor to an Author notifies them of a final decision regarding their submission.');
INSERT INTO `email_templates_default_data` VALUES ('EDITOR_DECISION_RESUBMIT','en_US','Editor Decision','{$authorName}:\n\nWe have reached a decision regarding your submission to {$journalTitle}, \"{$articleTitle}\".\n\nOur decision is to:\n\n{$editorialContactSignature}\n','This email from the Editor or Section Editor to an Author notifies them of a final decision regarding their submission.');
INSERT INTO `email_templates_default_data` VALUES ('EDITOR_DECISION_DECLINE','en_US','Editor Decision','{$authorName}:\n\nWe have reached a decision regarding your submission to {$journalTitle}, \"{$articleTitle}\".\n\nOur decision is to:\n\n{$editorialContactSignature}\n','This email from the Editor or Section Editor to an Author notifies them of a final decision regarding their submission.');
INSERT INTO `email_templates_default_data` VALUES ('COPYEDIT_REQUEST','en_US','Copyediting Request','{$copyeditorName}:\n\nI would ask that you undertake the copyediting of \"{$articleTitle}\" for {$journalName} by following these steps.\n1. Click on the Submission URL below.\n2. Log into the journal and click on the File that appears in Step 1.\n3. Consult Copyediting Instructions posted on webpage.\n4. Open the downloaded file and copyedit, while adding Author Queries as needed. \n5. Save copyedited file, and upload to Step 1 of Copyediting.\n6. Send the COMPLETE email to the editor.\n\n{$journalName} URL: {$journalUrl}\nSubmission URL: {$submissionCopyeditingUrl}\nUsername: {$copyeditorUsername}\n\n{$editorialContactSignature}','This email is sent by a Section Editor to a submission\'s Copyeditor to request that they begin the copyediting process. It provides information about the submission and how to access it.');
INSERT INTO `email_templates_default_data` VALUES ('EDITOR_DECISION_ACCEPT','en_US','Editor Decision','{$authorName}:\n\nWe have reached a decision regarding your submission to {$journalTitle}, \"{$articleTitle}\".\n\nOur decision is to:\n\n{$editorialContactSignature}\n','This email from the Editor or Section Editor to an Author notifies them of a final decision regarding their submission.');
INSERT INTO `email_templates_default_data` VALUES ('REVIEW_REMIND_AUTO_ONECLICK','en_US','Automated Submission Review Reminder','{$reviewerName}:\n\nJust a gentle reminder of our request for your review of the submission, \"{$articleTitle},\" for {$journalName}. We were hoping to have this review by {$reviewDueDate}, and this email has been automatically generated and sent with the passing of that date. We would still be pleased to receive it as soon as you are able to prepare it.\n\nSubmission URL: {$submissionReviewUrl}\n\nPlease confirm your ability to complete this vital contribution to the work of the journal. I look forward to hearing from you.\n\n{$editorialContactSignature}','This email is automatically sent when a reviewer\'s due date elapses (see Review Options under Journal Setup, Step 2) and one-click reviewer access is enabled. Scheduled tasks must be enabled and configured (see the site configuration file).');
INSERT INTO `email_templates_default_data` VALUES ('REVIEW_REMIND_ONECLICK','en_US','Submission Review Reminder','{$reviewerName}:\n\nJust a gentle reminder of our request for your review of the submission, \"{$articleTitle},\" for {$journalName}. We were hoping to have this review by {$reviewDueDate}, and would be pleased to receive it as soon as you are able to prepare it.\n\nSubmission URL: {$submissionReviewUrl}\n\nPlease confirm your ability to complete this vital contribution to the work of the journal. I look forward to hearing from you.\n\n{$editorialContactSignature}','This email is sent by a Section Editor to remind a reviewer that their review is due.');
INSERT INTO `email_templates_default_data` VALUES ('REVIEW_REMIND_AUTO','en_US','Automated Submission Review Reminder','{$reviewerName}:\n\nJust a gentle reminder of our request for your review of the submission, \"{$articleTitle},\" for {$journalName}. We were hoping to have this review by {$reviewDueDate}, and this email has been automatically generated and sent with the passing of that date. We would still be pleased to receive it as soon as you are able to prepare it.\n\nIf you do not have your username and password for the journal\'s web site, you can use this link to reset your password (which will then be emailed to you along with your username). {$passwordResetUrl}\n\nSubmission URL: {$submissionReviewUrl}\n\nPlease confirm your ability to complete this vital contribution to the work of the journal. I look forward to hearing from you.\n\n{$editorialContactSignature}','This email is automatically sent when a reviewer\'s due date elapses (see Review Options under Journal Setup, Step 2) and one-click reviewer access is disabled. Scheduled tasks must be enabled and configured (see the site configuration file).');
INSERT INTO `email_templates_default_data` VALUES ('REVIEW_REMIND','en_US','Submission Review Reminder','{$reviewerName}:\n\nJust a gentle reminder of our request for your review of the submission, \"{$articleTitle},\" for {$journalName}. We were hoping to have this review by {$reviewDueDate}, and would be pleased to receive it as soon as you are able to prepare it.\n\nIf you do not have your username and password for the journal\'s web site, you can use this link to reset your password (which will then be emailed to you along with your username). {$passwordResetUrl}\n\nSubmission URL: {$submissionReviewUrl}\n\nPlease confirm your ability to complete this vital contribution to the work of the journal. I look forward to hearing from you.\n\n{$editorialContactSignature}','This email is sent by a Section Editor to remind a reviewer that their review is due.');
INSERT INTO `email_templates_default_data` VALUES ('REVIEW_DECLINE','en_US','Unable to Review','{$editorialContactName}:\n\nI am afraid that at this time I am unable to review the submission, \"{$articleTitle},\" for {$journalName}. Thank you for thinking of me, and another time feel free to call on me.\n\n{$reviewerName}','This email is sent by a Reviewer to the Section Editor in response to a review request to notify the Section Editor that the review request has been declined.');
INSERT INTO `email_templates_default_data` VALUES ('REVIEW_COMPLETE','en_US','Article Review Completed','{$editorialContactName}:\n\nI have now completed my review of \"{$articleTitle}\" for {$journalName}, and submitted my recommendation, \"{$recommendation}.\"\n\n{$reviewerName}','This email is sent by a Reviewer to the Section Editor to notify them that a review has been completed and the comments and recommendations have been recorded on the journal web site.');
INSERT INTO `email_templates_default_data` VALUES ('REVIEW_ACK','en_US','Article Review Acknowledgement','{$reviewerName}:\n\nThank you for completing the review of the submission, \"{$articleTitle},\" for {$journalName}. We appreciate your contribution to the quality of the work that we publish.\n\n{$editorialContactSignature}','This email is sent by a Section Editor to confirm receipt of a completed review and thank the reviewer for their contributions.');
INSERT INTO `email_templates_default_data` VALUES ('REVIEW_CONFIRM','en_US','Able to Review','{$editorialContactName}:\n\nI am able and willing to review the submission, \"{$articleTitle},\" for {$journalName}. Thank you for thinking of me, and I plan to have the review completed by its due date, {$reviewDueDate}, if not before.\n\n{$reviewerName}','This email is sent by a Reviewer to the Section Editor in response to a review request to notify the Section Editor that the review request has been accepted and will be completed by the specified date.');
INSERT INTO `email_templates_default_data` VALUES ('REVIEW_REQUEST_ATTACHED','en_US','Article Review Request','{$reviewerName}:\n\nI believe that you would serve as an excellent reviewer of the manuscript, \"{$articleTitle},\" and I am asking that you consider undertaking this important task for us. The Review Guidelines for this journal are appended below, and the submission is attached to this email. Your review of the submission, along with your recommendation, should be emailed to me by {$reviewDueDate}.\n\nPlease indicate in a return email by {$weekLaterDate} whether you are able and willing to do the review.\n\nThank you for considering this request.\n\n{$editorialContactSignature}\n\n\nReview Guidelines\n\n{$reviewGuidelines}\n','This email is sent by the Section Editor to a Reviewer to request that they accept or decline the task of reviewing a submission. It includes the submission as an attachment. This message is used when the Email-Attachment Review Process is selected in Journal Setup, Step 2. (Otherwise see REVIEW_REQUEST.)');
INSERT INTO `email_templates_default_data` VALUES ('REVIEW_CANCEL','en_US','Request for Review Cancelled','{$reviewerName}:\n\nWe have decided at this point to cancel our request for you to review the submission, \"{$articleTitle},\" for {$journalName}. We apologize for any inconvenience this may cause you and hope that we will be able to call on you to assist with this journal\'s review process in the future.\n\nIf you have any questions, please contact me.\n\n{$editorialContactSignature}','This email is sent by the Section Editor to a Reviewer who has a submission review in progress to notify them that the review has been cancelled.');
INSERT INTO `email_templates_default_data` VALUES ('REVIEW_REQUEST_ONECLICK','en_US','Article Review Request','{$reviewerName}:\n\nI believe that you would serve as an excellent reviewer of the manuscript, \"{$articleTitle},\" which has been submitted to {$journalName}. The submission\'s abstract is inserted below, and I hope that you will consider undertaking this important task for us.\n\nPlease log into the journal web site by {$weekLaterDate} to indicate whether you will undertake the review or not, as well as to access the submission and to record your review and recommendation.\n\nThe review itself is due {$reviewDueDate}.\n\nSubmission URL: {$submissionReviewUrl}\n\nThank you for considering this request.\n\n{$editorialContactSignature}\n\n\n\n\"{$articleTitle}\"\n\n{$abstractTermIfEnabled}\n{$articleAbstract}','This email from the Section Editor to a Reviewer requests that the reviewer accept or decline the task of reviewing a submission. It provides information about the submission such as the title and abstract, a review due date, and how to access the submission itself. This message is used when the Standard Review Process is selected in Journal Setup, Step 2, and one-click reviewer access is enabled.');
INSERT INTO `email_templates_default_data` VALUES ('REVIEW_REQUEST','en_US','Article Review Request','{$reviewerName}:\n\nI believe that you would serve as an excellent reviewer of the manuscript, \"{$articleTitle},\" which has been submitted to {$journalName}. The submission\'s abstract is inserted below, and I hope that you will consider undertaking this important task for us.\n\nPlease log into the journal web site by {$weekLaterDate} to indicate whether you will undertake the review or not, as well as to access the submission and to record your review and recommendation. The web site is {$journalUrl}\n\nThe review itself is due {$reviewDueDate}.\n\nIf you do not have your username and password for the journal\'s web site, you can use this link to reset your password (which will then be emailed to you along with your username). {$passwordResetUrl}\n\nSubmission URL: {$submissionReviewUrl}\n\nThank you for considering this request.\n\n{$editorialContactSignature}\n\n\n\n\"{$articleTitle}\"\n\n{$abstractTermIfEnabled}\n{$articleAbstract}','This email from the Section Editor to a Reviewer requests that the reviewer accept or decline the task of reviewing a submission. It provides information about the submission such as the title and abstract, a review due date, and how to access the submission itself. This message is used when the Standard Review Process is selected in Journal Setup, Step 2. (Otherwise see REVIEW_REQUEST_ATTACHED.)');
INSERT INTO `email_templates_default_data` VALUES ('SUBMISSION_UNSUITABLE','en_US','Unsuitable Submission','{$authorName}:\n\nAn initial review of \"{$articleTitle}\" has made it clear that this submission does not fit within the scope and focus of {$journalName}. I recommend that you consult the description of this journal under About, as well as its current contents, to learn more about the work that we publish. You might also consider submitting this manuscript to another, more suitable journal.\n\n{$editorialContactSignature}','');
INSERT INTO `email_templates_default_data` VALUES ('SUBMISSION_COMMENT','en_US','Submission Comment','{$name}:\n\n{$commentName} has added a comment to the submission, \"{$articleTitle}\" in {$journalName}:\n\n{$comments}','This email notifies the various people involved in a submission\'s editing process that a new comment has been posted.');
INSERT INTO `email_templates_default_data` VALUES ('SUBMISSION_DECISION_REVIEWERS','en_US','Decision on \"{$articleTitle}\"','As one of the reviewers for the submission, \"{$articleTitle},\" to {$journalName}, I am sending you the reviews and editorial decision sent to the author of this piece. Thank you again for your important contribution to this process.\n \n{$editorialContactSignature}\n\n{$comments}','This email notifies the reviewers of a submission that the review process has been completed. It includes information about the article and the decision reached, and thanks the reviewers for their contributions.');
INSERT INTO `email_templates_default_data` VALUES ('EDITOR_ASSIGN','en_US','Editorial Assignment','{$editorialContactName}:\n\nThe submission, \"{$articleTitle},\" to {$journalName} has been assigned to you to see through the editorial process in your role as Section Editor.  \n\nSubmission URL: {$submissionUrl}\nUsername: {$editorUsername}\n\nThank you,\n{$editorialContactSignature}','This email notifies a Section Editor that the Editor has assigned them the task of overseeing a submission through the editing process. It provides information about the submission and how to access the journal site.');
INSERT INTO `email_templates_default_data` VALUES ('SUBMISSION_ACK','en_US','Submission Acknowledgement','{$authorName}:\n\nThank you for submitting the manuscript, \"{$articleTitle}\" to {$journalName}. With the online journal management system that we are using, you will be able to track its progress through the editorial process by logging in to the journal web site:\n\nManuscript URL: {$submissionUrl}\nUsername: {$authorUsername}\n\nIf you have any questions, please contact me. Thank you for considering this journal as a venue for your work.\n\n{$editorialContactSignature}','This email, when enabled, is automatically sent to an author when he or she completes the process of submitting a manuscript to the journal. It provides information about tracking the submission through the process and thanks the author for the submission.');
INSERT INTO `email_templates_default_data` VALUES ('LOCKSS_NEW_ARCHIVE','en_US','Archiving Request for {$journalName}','Dear [University Librarian]\n\n{$journalName} <{$journalUrl}>, is a journal for which a member of your faculty, [name of member] serves as a [title of position]. The journal is seeking to establish a LOCKSS (Lots of Copies Keep Stuff Safe) compliant archive with this and other university libraries.\n\n[Brief description of journal]\n\nThe LOCKSS Program <http://lockss.org/>, an international library/publisher initiative, is a working example of a distributed preservation and archiving repository, additional details are below. The software, which runs on an ordinary personal computer is free; the system is easily brought on-line; very little ongoing maintenance is required.\n\nTo assist in the archiving of our journal, we invite you to become a member of the LOCKSS community, to help collect and preserve titles produced by your faculty and by other scholars worldwide. To do so, please have someone on your staff visit the LOCKSS site for information on how this system operates. I look forward to hearing from you on the feasibility of providing this archiving support for this journal.\n\nThank you,\n{$principalContactSignature}','This email encourages the recipient to participate in the LOCKSS initiative and include this journal in the archive. It provides information about the LOCKSS initiative and ways to become involved.');
INSERT INTO `email_templates_default_data` VALUES ('LOCKSS_EXISTING_ARCHIVE','en_US','Archiving Request for {$journalName}','Dear [University Librarian]\n\n{$journalName} <{$journalUrl}>, is a journal for which a member of your faculty, [name of member], serves as a [title of position]. The journal is seeking to establish a LOCKSS (Lots of Copies Keep Stuff Safe) compliant archive with this and other university libraries.\n\n[Brief description of journal]\n\nThe URL to the LOCKSS Publisher Manifest for our journal is: {$journalUrl}/gateway/lockss\n\nWe understand that you are already participating in LOCKSS. If we can provide any additional metadata for purposes of registering our journal with your version of LOCKSS, we would be happy to provide it.\n\nThank you,\n{$principalContactSignature}','This email requests the keeper of a LOCKSS archive to consider including this journal in their archive. It provides the URL to the journal\'s LOCKSS Publisher Manifest.');
INSERT INTO `email_templates_default_data` VALUES ('USER_REGISTER','en_US','Journal Registration','{$userFullName}\n\nYou have now been registered as a user with {$journalName}. We have included your username and password in this email, which are needed for all work with this journal through its website. At any point, you can ask to be removed from the journal\'s list of users by contacting me.\n\nUsername: {$username}\nPassword: {$password}\n\nThank you,\n{$principalContactSignature}','This email is sent to a newly registered user to welcome them to the system and provide them with a record of their username and password.');
INSERT INTO `email_templates_default_data` VALUES ('USER_VALIDATE','en_US','Validate Your Account','{$userFullName}\n\nYou have created an account with {$journalName}, but before you can start using it, you need to validate your email account. To do this, simply follow the link below:\n\n{$activateUrl}\n\nThank you,\n{$principalContactSignature}','This email is sent to a newly registered user to welcome them to the system and provide them with a record of their username and password.');
INSERT INTO `email_templates_default_data` VALUES ('REVIEWER_REGISTER','en_US','Registration as Reviewer with {$journalName}','In light of your expertise, we have taken the liberty of registering your name in the reviewer database for {$journalName}. This does not entail any form of commitment on your part, but simply enables us to approach you with a submission to possibly review. On being invited to review, you will have an opportunity to see the title and abstract of the paper in question, and you\'ll always be in a position to accept or decline the invitation. You can also ask at any point to have your name removed from this reviewer list.\n\nWe are providing you with a username and password, which is used in all interactions with the journal through its website. You may wish, for example, to update your profile, including your reviewing interests.\n\nUsername: {$username}\nPassword: {$password}\n\nThank you,\n{$principalContactSignature}','This email is sent to a newly registered reviewer to welcome them to the system and provide them with a record of their username and password.');
INSERT INTO `email_templates_default_data` VALUES ('PUBLISH_NOTIFY','en_US','New Issue Published','Readers:\n\n{$journalName} has just published its latest issue at {$journalUrl}. We invite you to review the Table of Contents here and then visit our web site to review articles and items of interest.\n\nThanks for the continuing interest in our work,\n{$editorialContactSignature}','This email is sent to registered readers via the \"Notify Users\" link in the Editor\'s User Home. It notifies readers of a new issue and invites them to visit the journal at a supplied URL.');
INSERT INTO `email_templates_default_data` VALUES ('NOTIFICATION_MAILLIST_PASSWORD','en_US','Your notification mailing list information for {$siteTitle}','Your new password for disabling notification emails is: {$password}\n\nIf you wish to stop receiving notification emails, please go to {$unsubscribeLink} and enter your email address and password.\n\n{$principalContactSignature}','This email is sent to an unregistered user on the notification mailing list when they indicate that they have forgotten their password or are unable to login. It provides a URL they can follow to reset their password.');
INSERT INTO `email_templates_default_data` VALUES ('PASSWORD_RESET_CONFIRM','en_US','Password Reset Confirmation','We have received a request to reset your password for the {$siteTitle} web site.\n\nIf you did not make this request, please ignore this email and your password will not be changed. If you wish to reset your password, click on the below URL.\n\nReset my password: {$url}\n\n{$principalContactSignature}','This email is sent to a registered user when they indicate that they have forgotten their password or are unable to login. It provides a URL they can follow to reset their password.');
INSERT INTO `email_templates_default_data` VALUES ('PASSWORD_RESET','en_US','Password Reset','Your password has been successfully reset for use with the {$siteTitle} web site. Please retain this username and password, as it is necessary for all work with the journal.\n\nYour username: {$username}\nYour new password: {$password}\n\n{$principalContactSignature}','This email is sent to a registered user when they have successfully reset their password following the process described in the PASSWORD_RESET_CONFIRM email.');
INSERT INTO `email_templates_default_data` VALUES ('NOTIFICATION_MAILLIST_WELCOME','en_US','Welcome to the the {$siteTitle} mailing list!','You have signed up to receive notifications from {$siteTitle}.\n			\nPlease click on this link to confirm your request and add your email address to the mailing list: {$confirmLink}\n\nIf you wish to stop receiving notification emails, please go to {$unsubscribeLink} and enter your email address and password.\n\nYour password for disabling notification emails is: {$password}\n\n{$principalContactSignature}','This email is sent to an unregistered user who just registered with the notification mailing list.');
INSERT INTO `email_templates_default_data` VALUES ('NOTIFICATION','en_US','New notification from {$siteTitle}','You have a new notification from {$siteTitle}:\n\n{$notificationContents}\n\nLink: {$url}\n\n{$principalContactSignature}','The email is sent to registered users that have selected to have this type of notification emailed to them.');
INSERT INTO `email_templates_default_data` VALUES ('NOTIFICATION_MAILLIST','en_US','New notification from {$siteTitle}','You have a new notification from {$siteTitle}:\n--\n{$notificationContents}\n\nLink: {$url}\n--\n\nIf you wish to stop receiving notification emails, please go to {$unsubscribeLink} and enter your email address and password.\n\n{$principalContactSignature}','This email is sent to unregistered users on the notification mailing list.');
