INSERT INTO `rt_searches` VALUES (90,14,'Google Scholar','Google Scholar enables specific searches of scholarly literature, including peer-reviewed papers, theses, books, pre-prints, abstracts, and technical reports. Content includes a range of publishers and aggregators with whom Google already has standing arrangements, e.g., the Association for Computing Machinery, IEEE, OCLC\'s Open WorldCat library locator service, etc. Result displays will show different version clusters, citation analysis, and library location (currently books only).','http://scholar.google.com','http://scholar.google.com/scholar?ie=UTF-8&oe=UTF-8&hl=en&q={$formKeywords}',NULL,1);
