INSERT INTO `rt_searches` VALUES (92,14,'Vivisimo','The award-winning Vivisimo clustering technology, developed by Carnegie Mellon research scientists, is unlocking the value of stored information at Fortune 500 companies, top websites, prestigious publishers and government agencies by categorizing research results on the fly into meaningful orders, thus achieving breakthrough improvement in access to relevant information.','http://vivisimo.com','http://vivisimo.com/search?tb=homepage&v%3Asources=Web&query={$formKeywords}',NULL,3);
