INSERT INTO `rt_searches` VALUES (349,59,'CASI Technical Report Server','CASI Technical Report Server contains bibliographic citations and abstracts to unclassified documents announced in Scientific and technical aerospace reports, plus the document series produced by NASA\'s predecessor, The National Advisory Committee for Aeronautics, and the NASA open literature file containing citations and abstracts to literature in the fields of aeronautics, space science, chemistry, engineering, geosciences, life sciences, mathematics, computer sciences, and physics.','http://www.sti.nasa.gov/RECONselect.html','http://ntrs.nasa.gov/index.cgi?method=search&limit=25&offset=0&mode=simple&order=DESC&keywords={$formKeywords}',NULL,5);
INSERT INTO `rt_searches` VALUES (350,59,'GrayLIT Network','GrayLIT Network provides a full-text search across the gray literature of multiple government agencies within a science portal of technical reports. It taps into the search engines of distributed gray literature collections, enabling the user to find information without first having to know the sponsoring agency.','http://graylit.osti.gov/','http://graylit.osti.gov/cgi-bin/dexplcgi','pp=all&INTERFACE=1WINDOW&FORM=DistributedSearch.html&COLLECTION=dticft&COLLECTION=jpl&COLLECTION=gpoinfo&COLLECTION=langley&MAXDOCS=50&QUERY={$formKeywords}',6);
INSERT INTO `rt_searches` VALUES (351,59,'KOSMOI','KOSMOI includes educational articles, books, posters, & web links for all interested in the wonders of our Cosmos, on Science, Space, Technology, Nature, & Web Design. Updated daily.','http://kosmoi.com/','http://www.google.com/custom?sa=Search&cof=GIMP%3A%23cccccc%3BT%3A%23ffdd99%3BLW%3A468%3BALC%3Ared%3BL%3Ahttp%3A%2F%2Fencyclozine.com%2FPictures%2FBanner%2FEncycloZine2.jpg%3BGFNT%3A%23999999%3BLC%3A%23ffcc33%3BLH%3A60%3BBGC%3Ablack%3BAH%3Acenter%3BVLC%3A%23ffcc66%3BGL%3A2%3BGALT%3A%23ffffff%3BAWFID%3A29728ead1ae72975%3B&domains=EncycloZine.com%3BKosmoi.com%3BEluzions.com&sitesearch=Kosmoi.com&q={$formKeywords}',NULL,7);
