INSERT INTO `rt_searches` VALUES (398,68,'NASA Technical Reports Server','NASA Technical Reports Server allows users to search available online NASA published documents, including meeting presentations, journal articles, conference proceedings, and technical reports. Many documents are available in compressed PostScript and PDF formats. All documents are unclassified and publicly available.','http://ntrs.nasa.gov/?method=aboutntrs','http://ntrs.nasa.gov/search.jsp?N=0&Ntk=all&Ntx=mode%20matchall&Ntt={$formKeywords}',NULL,9);
INSERT INTO `rt_searches` VALUES (399,68,'HighWire','HighWire is one of the highest-impact journals in the field of science, technology and medicine. As of March 2002 HighWire contained 11,785,877 articles in over 4,500 Medline journals and 405,385 free full text articles from 321 HighWire-based journals.','http://highwire.stanford.edu/','http://highwire.stanford.edu/cgi/searchresults?andorexactfulltext=and&resourcetype=1&src=hw&fulltext=&pubdate_year=&volume=&firstpage=&disp_type=&author1={$formKeywords}',NULL,10);
