INSERT INTO `rt_searches` VALUES (499,91,'Mail & Guardian Newspaper','Mail & Guardian Newspaper, South Africa (1994-)','http://www.mg.co.za/','http://www.mg.co.za/mg_search_results.aspx?PrintEdition=PrintEdition&DailyNews=DailyNews&SearchSection=&StartDay=&StartMonth=&StartYear=&EndDay=&EndMonth=&EndYear=&keywords={$formKeywords}',NULL,3);
INSERT INTO `rt_searches` VALUES (500,91,'National Public Radio','National Public Radio, United States (unlimited)','http://www.npr.org/archives/','http://www.npr.org/search.php?text={$formKeywords}',NULL,4);
