INSERT INTO `rt_searches` VALUES (502,91,'The Japan Times Online','The Japan Times Online, Japan (January 1999-)','http://www.japantimes.co.jp/','http://www.google.co.jp/custom?domains=japantimes.co.jp&client=pub-4223870936880387&forid=1&ie=Shift_JIS&oe=Shift_JIS&term1=&cof=GALT%3A%23008000%3BGL%3A1%3BDIV%3A%23336699%3BVLC%3A663399%3BAH%3Acenter%3BBGC%3AFFFFFF%3BLBGC%3AFFFFFF%3BALC%3A0000FF%3BLC%3A0000FF%3BT%3A000000%3BGFNT%3A0000FF%3BGIMP%3A0000FF%3BLH%3A60%3BLW%3A200%3BL%3Ahttp%3A%2F%2Fwww.japantimes.co.jp%2Fimages%2Fheader_title.gif%3BS%3Ahttp%3A%2F%2Fwww.japantimes.co.jp%2F%3BFORID%3A1%3B&hl=ja&advancesearch=&q={$formKeywords}',NULL,6);
INSERT INTO `rt_searches` VALUES (503,91,'The Moscow Times','The Moscow Times, Russia (1994-)','http://www.moscowtimes.ru/doc/Search.html','http://www.moscowtimes.ru/cgi-bin/search?config=&exclude=&method=and&format=long&sort=score&matchesperpage=10&words={$formKeywords}',NULL,7);
INSERT INTO `rt_searches` VALUES (504,91,'Washington Post','Washington Post, Washington, DC (last two weeks)','http://www.washingtonpost.com/','http://www.washingtonpost.com/cgi-bin/search99.pl?searchsection=news&searchdatabase=news&x=11&y=8&searchtext={$formKeywords}',NULL,8);
INSERT INTO `rt_searches` VALUES (505,91,'Newsdirectory','Newsdirectory is a comprehensive and searchable guide to the world\'s English-language online media. Currently over 8,000 newspapers and magazines are listed, as well as more than 1,000 U.S. television broadcasters.','http://www.newsdirectory.com/','http://www.newsdirectory.com/hbSearch.php','s={$formKeywords}& submit=Go',9);
INSERT INTO `rt_searches` VALUES (506,92,'Google','Search using the popular Google search engine.','http://www.google.com/','http://www.google.com/search?q={$formKeywords}',NULL,0);
INSERT INTO `rt_searches` VALUES (507,92,'Google Scholar','Google Scholar enables specific searches of scholarly literature, including peer-reviewed papers, theses, books, pre-prints, abstracts, and technical reports. Content includes a range of publishers and aggregators with whom Google already has standing arrangements, e.g., the Association for Computing Machinery, IEEE, OCLC\'s Open WorldCat library locator service, etc. Result displays will show different version clusters, citation analysis, and library location (currently books only).','http://scholar.google.com','http://scholar.google.com/scholar?ie=UTF-8&oe=UTF-8&hl=en&q={$formKeywords}',NULL,1);
INSERT INTO `rt_searches` VALUES (508,92,'Clusty the Clustering Engine','This search tool from Vivismo offers clustered results for a selection of searches.  Metasearch the whole web, or use tabs to search for news, gossip, images, orproducts via Bizrate or eBay.','http://clusty.com/about','http://clusty.com/search?query={$formKeywords}',NULL,2);
INSERT INTO `rt_searches` VALUES (509,92,'Vivisimo','The award-winning Vivisimo clustering technology, developed by Carnegie Mellon research scientists, is unlocking the value of stored information at Fortune 500 companies, top websites, prestigious publishers and government agencies by categorizing research results on the fly into meaningful orders, thus achieving breakthrough improvement in access to relevant information.','http://vivisimo.com','http://vivisimo.com/search?tb=homepage&v%3Asources=Web&query={$formKeywords}',NULL,3);
INSERT INTO `rt_searches` VALUES (510,93,'Google Scholar','Google Scholar enables specific searches of scholarly literature, including peer-reviewed papers, theses, books, pre-prints, abstracts, and technical reports. Content includes a range of publishers and aggregators with whom Google already has standing arrangements, e.g., the Association for Computing Machinery, IEEE, OCLC\'s Open WorldCat library locator service, etc. Result displays will show different version clusters, citation analysis, and library location (currently books only).','http://scholar.google.com','http://scholar.google.com/scholar?ie=UTF-8&oe=UTF-8&hl=eng&as_sauthors={$formKeywords}',NULL,0);
INSERT INTO `rt_searches` VALUES (511,93,'Intute: Social Sciences','Intute is a free online service providing you with access to the very best Web resources for education and research, evaluated and selected by a network of subject specialists.','http://www.intute.ac.uk/socialsciences//','http://www.intute.ac.uk/socialsciences/cgi-bin/search.pl?submit.x=0&submit.y=0&submit=Go&limit=0&subject=socialsciences&term1={$formKeywords}',NULL,1);
INSERT INTO `rt_searches` VALUES (512,93,'Intute: Repository Search','Use this service to find descriptions from over 152,000 working papers, journal articles, reports, conference papers, and other scholarly items that have been deposited into UK eprints repositories. Search results will link to original items where these have been made available by the originating institution.','http://irs.ukoln.ac.uk/','http://irs.ukoln.ac.uk/search/?view=&submit.x=0&submit.y=0&submit=Go&query={$formKeywords}',NULL,2);
INSERT INTO `rt_searches` VALUES (513,93,'OAIster (Open Archives Initiative research databases)','OAIster is a project of the University of Michigan Digital Library Production Services, and provides searching a wide variety of collections from a wide variety of institutions. These institutions have made the records of their digital resources available to access, and the project team has gathered and aggregated them into the OAIster service.','http://oaister.umdl.umich.edu/','http://oaister.umdl.umich.edu/cgi/b/bib/bib-idx?type=boolean&size=10&c=oaister&q1=&rgn1=entire+record&op2=and&q2=&rgn2=entire+record&op3=And&q3=&rgn3=title&op4=And&rgn4=author&op5=And&q5=&rgn5=subject&op6=And&rgn6=norm&q6=all+types&sort=author%2Fcreator&submit2=search&q4={$formKeywords}',NULL,3);
INSERT INTO `rt_searches` VALUES (514,93,'Public Knowledge Project Open Archives Harvester','The PKP Open Archives Harvester is a free metadata indexing system developed by the Public Knowledge Project through its federally funded efforts to expand and improve access to research. The PKP OAI Harvester allows you to create a searchable index of the metadata from Open Archives Initiative-compliant archives, such as sites using Open Journal Systems or Open Conference Systems.','http://pkp.sfu.ca/harvester/','http://pkp.sfu.ca/harvester/search.php?limit=author&query={$formKeywords}',NULL,4);
INSERT INTO `rt_searches` VALUES (515,93,'IDEAc','IDEA: the complete RePEc database at your disposal. Working papers, journal articles, software components, author information, directory of institutions.','http://ideas.repec.org/','http://ideas.repec.org/cgi-bin/htsearch?restrict=http://ideas.repec.org/p/&config=htdig&restrict=&exclude=&sort=score&format=long&method=and&search_algorithm=exact:1&words={$formKeywords}',NULL,5);
INSERT INTO `rt_searches` VALUES (516,93,'World Bank Group Documents & Reports','The World Bank Group makes more than 14,000 documents available through the Documents & Reports website. Documents include Project appraisal reports, Economic and Sector Works, Evaluation reports and studies and working papers.','http://www-wds.worldbank.org/','http://www-wds.worldbank.org/servlet/WDS_IBank_Servlet?all=&stype=AllWords&dname=&rc=&ss=&dt=&dr=range&bdt=&edt=&rno=&lno=&cno=&pid=&tno=&sortby=D&sortcat=D&psz=20&x=34&y=8&ptype=advSrch&pcont=results&auth={$formKeywords}',NULL,6);
INSERT INTO `rt_searches` VALUES (517,93,'NBER','NBER (The National Bureau of Economic Research, Inc) is a private, nonprofit, nonpartisan research organization dedicated to promoting a greater understanding of how the economy works. Nearly 500 NBER Working papers are published each year, and many subsequently appear in scholarly journals.','http://papers.nber.org/','http://papers.nber.org/papers?module=search&action=query&default_conjunction=and&keywords={$formKeywords}',NULL,7);
