INSERT INTO `rt_searches` VALUES (535,97,'Intute: Social Sciences','Intute is a free online service providing you with access to the very best Web resources for education and research, evaluated and selected by a network of subject specialists.','http://www.intute.ac.uk/socialsciences//','http://www.intute.ac.uk/socialsciences/cgi-bin/search.pl?submit.x=0&submit.y=0&submit=Go&limit=0&subject=socialsciences&term1={$formKeywords}',NULL,1);
INSERT INTO `rt_searches` VALUES (536,97,'Intute: Repository Search','Use this service to find descriptions from over 152,000 working papers, journal articles, reports, conference papers, and other scholarly items that have been deposited into UK eprints repositories. Search results will link to original items where these have been made available by the originating institution.','http://irs.ukoln.ac.uk/','http://irs.ukoln.ac.uk/search/?view=&submit.x=0&submit.y=0&submit=Go&query={$formKeywords}',NULL,2);
