INSERT INTO `rt_searches` VALUES (538,98,'WebEC: World Wide Web Resources in Economics','WebEC provides links to a variety of resources on economics. Topics include: economics and teaching; methodology and history; mathematical and quantitative methods; economics and computing; economics data; microeconomics; macroeconomics; international economics; financial economics; public economics; health, education and welfare; labor and demographics; law and economics; industrial organization; business economics; economic history; development, technological change and growth; economic systems; agriculture and natural resources; regional economics; and economics of networks.','http://www.helsinki.fi/WebEc/','http://www.google.com/search?hl=en&lr=&q=site%3Awww.helsinki.fi%2F WebEc%2F+{$formKeywords}',NULL,0);
INSERT INTO `rt_searches` VALUES (539,99,'Office for National Statistics (UK)','National Statistics is the official UK statistics site. You can view and download a wealth of economic and social data free.','http://www.statistics.gov.uk/','http://www.statistics.gov.uk/CCI/SearchRes.asp?term={$formKeywords}',NULL,0);
