INSERT INTO `rt_searches` VALUES (549,102,'biz/ed','Business Education on the Internet (biz/ed) is a free information service available via the World-Wide Web which allows users to search and retrieve targeted information about business and economics held on computers around the world. The service offers a one-stop information gateway for the one million economics, business and management students and staff as well as the general public in the UK and overseas.','http://www.bized.co.uk/','http://www.bized.co.uk/cgi-bin/htsearch?config=htdig&method=and&sort=score&format=builtin-long&restrict=&exclude=&words={$formKeywords}',NULL,1);
