INSERT INTO `rt_searches` VALUES (598,108,'Physics Documents Worldwide (PhysDoc)','Physics Documents Worldwide (PhysDoc) offers lists of links to document sources, such as preprints, research reports, annual reports, and list of publications of worldwide distributed physics institutions and individual physicists, ordered by continent, country and town.','http://physnet.uni-oldenburg.de/PhysNet/physdoc.html#search','http://www.physnet.de/PhysNet/physdocsearch.html?errorflag=0&caseflag=on&wordflag=on&maxobjflag=200&opaqueflag=on&descflag=on&csumflag=off&verbose=off&broker=PhysDoc&domain=physnet.de&query={$formKeywords}',NULL,5);
