INSERT INTO `rt_searches` VALUES (692,127,'Literature, Arts, and Medicine Databases','The Literature, Arts, & Medicine Database is an annotated multimedia listing of prose, poetry, film, video and art that was developed to be a dynamic, accessible, comprehensive resource for teaching and research in MEDICAL HUMANITIES, and for use in health/pre-health, graduate and undergraduate liberal arts and social science settings.','http://litmed.med.nyu.edu/Main?action=aboutDB','http://google.med.nyu.edu/search?btnG=search&access=p&entqr=0&output=xml_no_dtd&sort=date%3AD%3AL%3Ad1&ud=1&client=litmed_test&oe=UTF-8&ie=UTF-8&proxystylesheet=litmed_test&site=litmed_test&q={$formKeywords}',NULL,6);
