INSERT INTO `rt_searches` VALUES (788,146,'The Global Legal Information Network (GLIN)','The Global Legal Information Network (GLIN) is a database of laws, regulations, judicial decisions, and other complementary legal sources contributed by governmental agencies and international organizations. These GLIN members contribute the official full texts of published documents to the database in their original language. Each document is accompanied by a summary in English and subject terms selected from the multilingual index to GLIN.','http://www.glin.gov','http://www.glin.gov/search.do?refineQuery=&offset=0&refineQueryType=&fromSearch=true&queryType=ALL&searchBtn=Search&includeAllFields=on&searchAll=on&sumLanguage=any&pubLanguage=any&pubJurisdiction=any&publicationJurisdictionExclude=false&searchPublicationDate=true&dateFromDay=01&dateFromMonth=01&dateFromYear=&dateToDay=01&dateToMonth=01&dateToYear=&subjTerm=&subjTermConjunction%5B0%5D=AND&subjTerm=&subjTermConjunction%5B1%5D=AND&subjTerm=&subjTermConjunction%5B2%5D=AND&subjTerm=&subjTermConjunction%5B3%5D=AND&subjTerm=&searchOrder=default&hitsPerPage=10&showSummary=on&queryString={$formKeywords}',NULL,6);
INSERT INTO `rt_searches` VALUES (789,146,'Health Canada','Health Canada is the federal department responsible for helping the people of Canada maintain and improve their health.  Health Canada is committed to improving the lives of all of Canada\'s people and to making this country\'s population among the healthiest in the world as measured by longevity, lifestyle and effective use of the public health care system.','http://search.hc-sc.gc.ca/cgi-bin/query?mss=hcsearch','http://search.hc-sc.gc.ca/cgi-bin/query?mss=hcresult&pg=aq&enc=iso88591&ft=adverse+drug&doc=all&results=any&exclude=&r=&kl=en&subsite=both&search=Search&q={$formKeywords}',NULL,7);
