INSERT INTO `rt_searches` VALUES (935,173,'Newsdirectory','Newsdirectory is a comprehensive and searchable guide to the world\'s English-language online media. Currently over 8,000 newspapers and magazines are listed, as well as more than 1,000 U.S. television broadcasters.','http://www.newsdirectory.com/','http://www.newsdirectory.com/hbSearch.php','s={$formKeywords}& submit=Go',9);
INSERT INTO `rt_searches` VALUES (936,173,'The ArchNewsNow','The ArchNewsNow (ANN) newsletter, launched in February 2002, is delivered daily to subscribers -- free of charge -- via e-mail by 9:30AM (New York time). It hyperlinks directly to the latest news and commentary gleaned from sources around the world.','http://www.archnewsnow.com/','http://www.archnewsnow.com/cgi-local/search.pl?Required={$formKeywords}',NULL,10);
