INSERT INTO `rt_searches` VALUES (991,185,'The Scientist','The Scientist is the online resource for the printed magazine, The Scientist, and provides access to information useful to those working in or studying the life sciences.','http://www.the-scientist.com/','http://www.the-scientist.com/search/dosearch/','box_type=toolbar&search_restriction=all&order_by=date&search_terms={$formKeywords}',1);
INSERT INTO `rt_searches` VALUES (992,185,'Scientific American Archive','Scientific American Archive is the online science and technology resource offering access to every page and every issue of Scientific American magazine from 1993 to the present.','http://www.sciamdigital.com/','http://www.sciamdigital.com/index.cfm?fa=Search.ViewSearchForItemResultList&AUTHOR_CHAR=&TITLE_CHAR=&FullText_CHAR={$formKeywords}',NULL,2);
