INSERT INTO `rt_searches` VALUES (1000,187,'OAIster (Open Archives Initiative research databases)','OAIster is a project of the University of Michigan Digital Library Production Services, and provides searching a wide variety of collections from a wide variety of institutions. These institutions have made the records of their digital resources available to access, and the project team has gathered and aggregated them into the OAIster service.','http://oaister.umdl.umich.edu/','http://oaister.umdl.umich.edu/cgi/b/bib/bib-idx?type=boolean&size=10&c=oaister&q1=&rgn1=entire+record&op2=and&q2=&rgn2=entire+record&op3=And&q3=&rgn3=title&op4=And&rgn4=author&op5=And&q5=&rgn5=subject&op6=And&rgn6=norm&q6=all+types&sort=author%2Fcreator&submit2=search&q4={$formKeywords}',NULL,2);
INSERT INTO `rt_searches` VALUES (1001,187,'DOE Information Bridge','The Information Bridge provides the open source to full-text and bibliographic records of Department of Energy (DOE) research and development reports in physics, chemistry, materials, biology, environmental sciences, energy technologies, engineering, computer and information science, renewable energy, and other topics.','http://www.osti.gov/bridge/index.jsp','http://www.osti.gov/bridge/basicsearch.jsp?act=Search&formname=basicsearch.jsp&review=1&SortBy=RELV&SortOrder=DESC&querytype=search&searchFor={$formKeywords}',NULL,3);
INSERT INTO `rt_searches` VALUES (1002,187,'NCSTRL','NCSTRL is an international collection of computer science research reports and papers made available for non-commercial use from a number of participating institutions and archives. NCSTRL provides access to over 20,000 technical reports in computer science.','http://www.ncstrl.org:8900/ncstrl/index.html','http://www.ncstrl.org:8900/ncstrl/servlet/search?formname=simple&group=archive&sort=rank&fulltext={$formKeywords}',NULL,4);
INSERT INTO `rt_searches` VALUES (1003,187,'Public Knowledge Project Open Archives Harvester','The PKP Open Archives Harvester is a free metadata indexing system developed by the Public Knowledge Project through its federally funded efforts to expand and improve access to research. The PKP OAI Harvester allows you to create a searchable index of the metadata from Open Archives Initiative-compliant archives, such as sites using Open Journal Systems or Open Conference Systems.','http://pkp.sfu.ca/harvester/','http://pkp.sfu.ca/harvester/search.php?limit=author&query={$formKeywords}',NULL,5);
