INSERT INTO `rt_searches` VALUES (1076,202,'PubMed','This is an experimental interface to several databases published by the NLM. Included are Medline and Pre-Medline, Popline, Toxline, GenBank DNA sequences, GenBank Protein Sequences, BioMolecule 3D structures, and Complete Genomes. This resource contains links to the full text of the articles when available.','http://www.ncbi.nlm.nih.gov/entrez/query.fcgi?db=PubMed','http://www.ncbi.nlm.nih.gov/entrez/query.fcgi?db=PubMed&orig_db=PubMed&cmd=search&cmd_current=&query_key=1&term={$formKeywords}',NULL,7);
