INSERT INTO `rt_searches` VALUES (1177,223,'The Japan Times Online','The Japan Times Online, Japan (January 1999-)','http://www.japantimes.co.jp/','http://www.google.co.jp/custom?domains=japantimes.co.jp&client=pub-4223870936880387&forid=1&ie=Shift_JIS&oe=Shift_JIS&term1=&cof=GALT%3A%23008000%3BGL%3A1%3BDIV%3A%23336699%3BVLC%3A663399%3BAH%3Acenter%3BBGC%3AFFFFFF%3BLBGC%3AFFFFFF%3BALC%3A0000FF%3BLC%3A0000FF%3BT%3A000000%3BGFNT%3A0000FF%3BGIMP%3A0000FF%3BLH%3A60%3BLW%3A200%3BL%3Ahttp%3A%2F%2Fwww.japantimes.co.jp%2Fimages%2Fheader_title.gif%3BS%3Ahttp%3A%2F%2Fwww.japantimes.co.jp%2F%3BFORID%3A1%3B&hl=ja&advancesearch=&q={$formKeywords}',NULL,6);
INSERT INTO `rt_searches` VALUES (1178,223,'The Moscow Times','The Moscow Times, Russia (1994-)','http://www.moscowtimes.ru/doc/Search.html','http://www.moscowtimes.ru/cgi-bin/search?config=&exclude=&method=and&format=long&sort=score&matchesperpage=10&words={$formKeywords}',NULL,7);
