INSERT INTO `rt_searches` VALUES (1230,234,'Newsdirectory','Newsdirectory is a comprehensive and searchable guide to the world\'s English-language online media. Currently over 8,000 newspapers and magazines are listed, as well as more than 1,000 U.S. television broadcasters.','http://www.newsdirectory.com/','http://www.newsdirectory.com/hbSearch.php','s={$formKeywords}& submit=Go',9);
