INSERT INTO `rt_searches` VALUES (1232,235,'Google Scholar','Google Scholar enables specific searches of scholarly literature, including peer-reviewed papers, theses, books, pre-prints, abstracts, and technical reports. Content includes a range of publishers and aggregators with whom Google already has standing arrangements, e.g., the Association for Computing Machinery, IEEE, OCLC\'s Open WorldCat library locator service, etc. Result displays will show different version clusters, citation analysis, and library location (currently books only).','http://scholar.google.com','http://scholar.google.com/scholar?ie=UTF-8&oe=UTF-8&hl=en&q={$formKeywords}',NULL,1);
INSERT INTO `rt_searches` VALUES (1233,235,'Clusty the Clustering Engine','This search tool from Vivismo offers clustered results for a selection of searches.  Metasearch the whole web, or use tabs to search for news, gossip, images, orproducts via Bizrate or eBay.','http://clusty.com/about','http://clusty.com/search?query={$formKeywords}',NULL,2);
INSERT INTO `rt_searches` VALUES (1234,235,'Vivisimo','The award-winning Vivisimo clustering technology, developed by Carnegie Mellon research scientists, is unlocking the value of stored information at Fortune 500 companies, top websites, prestigious publishers and government agencies by categorizing research results on the fly into meaningful orders, thus achieving breakthrough improvement in access to relevant information.','http://vivisimo.com','http://vivisimo.com/search?tb=homepage&v%3Asources=Web&query={$formKeywords}',NULL,3);
